sys_script_include.script:
var FirstSI = Class.create();
FirstSI.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
    funcA: function() {
        var count = 0;
        var gr = new GlideRecord('incident');
        gr.addQuery('short_description', this.getParameter('sysparm_sd'));
        gr.query();
        while (gr.next()) {
            count++;
        }
        return count;
    },

    type: 'FirstSI'
});
